function uiCodemirrorDirective($timeout, uiCodemirrorConfig) {

  return {
    restrict: 'EA',
    require: '?ngModel',
    compile: function compile() {

      // Require CodeMirror
      if (angular.isUndefined(window.CodeMirror)) {
        throw new Error('ui-codemirror needs CodeMirror to work... (o rly?)');
      }

      return postLink;
    }
  };

  function postLink(scope, iElement, iAttrs, ngModel) {

    var codemirrorOptions = angular.extend(
      { value: iElement.text() },
      uiCodemirrorConfig.codemirror || {},
      scope.$eval(iAttrs.uiCodemirror),
      scope.$eval(iAttrs.uiCodemirrorOpts)
    );

    var codemirror = newCodemirrorEditor(iElement, codemirrorOptions);

    configOptionsWatcher(
      codemirror,
      iAttrs.uiCodemirror || iAttrs.uiCodemirrorOpts,
      scope
    );

    configNgModelLink(codemirror, ngModel, scope);

    configUiRefreshAttribute(codemirror, iAttrs.uiRefresh, scope);

    // Allow access to the CodeMirror instance through a broadcasted event
    // eg: $broadcast('CodeMirror', function(cm){...});
    scope.$on('CodeMirror', function(event, callback) {
      if (angular.isFunction(callback)) {
        callback(codemirror);
      } else {
        throw new Error('the CodeMirror event requires a callback function');
      }
    });

    // onLoad callback
    if (angular.isFunction(codemirrorOptions.onLoad)) {
      codemirrorOptions.onLoad(codemirror);
    }
  }

  function newCodemirrorEditor(iElement, codemirrorOptions) {
    var codemirrot;

    if (iElement[0].tagName === 'TEXTAREA') {
      // Might bug but still ...
      codemirrot = window.CodeMirror.fromTextArea(iElement[0], codemirrorOptions);
    } else {
      iElement.html('');
      codemirrot = new window.CodeMirror(function(cm_el) {
        iElement.append(cm_el);
      }, codemirrorOptions);
    }

    return codemirrot;
  }

  function configOptionsWatcher(codemirrot, uiCodemirrorAttr, scope) {
    if (!uiCodemirrorAttr) { return; }

    var codemirrorDefaultsKeys = Object.keys(window.CodeMirror.defaults);
    scope.$watch(uiCodemirrorAttr, updateOptions, true);
    function updateOptions(newValues, oldValue) {
      if (!angular.isObject(newValues)) { return; }
      codemirrorDefaultsKeys.forEach(function(key) {
        if (newValues.hasOwnProperty(key)) {

          if (oldValue && newValues[key] === oldValue[key]) {
            return;
          }

          codemirrot.setOption(key, newValues[key]);
        }
      });
    }
  }

  function configNgModelLink(codemirror, ngModel, scope) {
    if (!ngModel) { return; }
    // CodeMirror expects a string, so make sure it gets one.
    // This does not change the model.
    ngModel.$formatters.push(function(value) {
      if (angular.isUndefined(value) || value === null) {
        return '';
      } else if (angular.isObject(value) || angular.isArray(value)) {
        throw new Error('ui-codemirror cannot use an object or an array as a model');
      }
      return value;
    });


    // Override the ngModelController $render method, which is what gets called when the model is updated.
    // This takes care of the synchronizing the codeMirror element with the underlying model, in the case that it is changed by something else.
    ngModel.$render = function() {
      //Code mirror expects a string so make sure it gets one
      //Although the formatter have already done this, it can be possible that another formatter returns undefined (for example the required directive)
      var safeViewValue = ngModel.$viewValue || '';
      codemirror.setValue(safeViewValue);
    };


    // Keep the ngModel in sync with changes from CodeMirror
    codemirror.on('change', function(instance) {
      var newValue = instance.getValue();
      if (newValue !== ngModel.$viewValue) {
        scope.$evalAsync(function() {
          ngModel.$setViewValue(newValue);
        });
      }
    });
  }

  function configUiRefreshAttribute(codeMirror, uiRefreshAttr, scope) {
    if (!uiRefreshAttr) { return; }

    scope.$watch(uiRefreshAttr, function(newVal, oldVal) {
      // Skip the initial watch firing
      if (newVal !== oldVal) {
        $timeout(function() {
          codeMirror.refresh();
        });
      }
    });
  }

}
uiCodemirrorDirective.$inject = ["$timeout", "uiCodemirrorConfig"];

$(function() {
  $('a[href*=#]:not([href=#])').click(function() {
    if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
      var target = $(this.hash);
      target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
      if (target.length) {
        $('html,body').animate({
          scrollTop: target.offset().top
        }, 1000);
        return false;
      }
    }
  });
});

var EXAMPLES = {};
EXAMPLES.parameterExample = function(param, path) {
  var ret = '';
  if (param.example) {
    ret = param.example;
  } else if (param.format === 'date') {
    ret = '1987-09-23';
  } else if (param.format === 'date-time') {
    ret = '1987-09-23T18:30:00Z';
  } else if (param.type === 'integer') {
    ret = '123';
  } else if (param.type === 'number') {
    ret = '1.23';
  } else if (param.type === 'string') {
    ret = 'xyz';
  } else if (param.type === 'boolean') {
    ret = 'true';
  } else if (param.type === 'array') {
    var choices = [];
    if (param.enum) {
      choices = param.enum.filter(function(choice, idx) {return idx < 2});
    } else if (param.items) {
      if (param.items.type === 'string') {
        choices = ['foo', 'bar'];
      } else if (param.items.type === 'integer') {
        choices = ['1', '2', '3'];
      } else if (param.items.type === 'number') {
        choices = ['1.0', '2.0', '3.0'];
      } else if (param.items.type === 'boolean') {
        choices = ['true', 'false'];
      } else {
        choices = [parameterExample(param.items, path)];
      }
    }
    if (param.collectionFormat === 'csv') {
      ret = choices.join(',');
    } else if (param.collectionFormat === 'ssv') {
      ret = choices.join(' ');
    } else if (param.collectionFormat === 'tsv') {
      ret = choices.join('\\t');
    } else if (param.collectionFormat === 'pipes') {
      ret = choices.join('|');
    } else if (param.collectionFormat === 'multi') {
      ret = choices[0];
    } else {
      ret = choices.join();
    }
  }
  if (param.enum && param.type !== 'array') {
    ret = param.enum[0];
  }
  if (param.in === 'path') {
    ret = path.replace('{' + param.name + '}', ret);
  } else if (param.in === 'query') {
    ret = '?' + param.name + '=' + ret;
  } else if (param.in === 'formData') {
    ret = param.name + '=' + ret;
  } else if (param.in === 'header') {
    ret = param.name + ': ' + ret;
  }
  return ret;
}

EXAMPLES.schemaExample = function(schema, readable) {
  if (!schema) return '';
  if (schema.type === 'array') {
    return [EXAMPLES.schemaExample(schema.items, readable)]
  } else if (schema.type === 'object' || schema.properties) {
    var ret = {};
    if (schema.properties) {
      for (key in schema.properties) {
        ret[key] = EXAMPLES.schemaExample(schema.properties[key], readable)
      }
    }
    if (schema.additionalProperties) {
      var example = EXAMPLES.schemaExample(schema.additionalProperties, readable);
      ret['item1'] = example;
      ret['item2'] = example;
    }
    return ret;
  } else if (schema.type === 'integer') {
    return readable ? 123 : 0;
  } else if (schema.type === 'number') {
    return readable ? 1.23 : 0.0;
  } else if (schema.type === 'string') {
    return readable ? "xyz" : "string";
  } else if (schema.type === 'boolean') {
    return true;
  } 
}

EXAMPLES.resolveSchema = function(obj, schema) {
  if (typeof schema !== 'object') return schema;
  var ret = {};
  for (key in schema) {
    if (key === '$ref') {
      ret = EXAMPLES.resolveSchema(obj, EXAMPLES.resolveRef(obj, schema[key]));
      break;
    } else {
      ret[key] = EXAMPLES.resolveSchema(obj, schema[key]);
    }
  }
  return ret;
}

EXAMPLES.resolveRef = function(object, ref) {
  var parts = ref.split('/');
  parts.shift();
  var cur = object;
  parts.forEach(function(part) {
    cur = cur[part];
  });
  return cur === object ? null : cur;
}


var LUCY_DONE = function(callback) {
  return function(response, status, request) {
     var isJson = request.getResponseHeader('Content-Type').indexOf('application/json') !== -1;
     if (isJson && response.error) {
       callback(response);
     } else {
       callback(null, response);
     }
  }
}

var LUCY_FAIL = function(callback) {
  return function(xhr, details) {
    if (xhr.responseJSON) return callback(xhr.responseJSON);
    else callback({message: "Could not connect to LucyBot"});
  }
}

Lucy = {
  get: function(path, input, callback) {
    if (!callback) {
      callback = input;
      input = {};
    };
    var query = '';
    for (key in input) {
      if (!query) query = '?';
      else query += '&';
      query += key + '=' + encodeURIComponent(input[key]);
    }
    $.ajax({
      method: 'get',
      url: BASE_URL + path + query,
    }).done(LUCY_DONE(callback)).fail(LUCY_FAIL(callback))
  },
  post: function(path, input, callback) {
    if (!callback) {
      callback = input;
      input = {};
    };
    $.ajax({
      method: 'post',
      url: BASE_URL + path,
      data: JSON.stringify(input),
      headers: {'Content-Type': 'application/json'}
    }).done(LUCY_DONE(callback)).fail(LUCY_FAIL(callback));
  }
}

