var maybeAddExternalDocs = function(description, externalDocs) {
  if (!externalDocs || !externalDocs.url) return description;
  if (description) description += '\n\n';
  else description = '';
  var url = document.createElement('a');
  url.href = externalDocs.url;
  description += 'Read more at [' + url.hostname + '](' + url.href + ')';
  return description;
}

var maybeTruncateSummary = function(operation) {
  if (!operation.summary) return;
  if (operation.summary.length <= 120) return;
  if (operation.description) return;
  var firstSentence = operation.summary.split(/\.\s/)[0];
  if (firstSentence.length > 120) firstSentence = firstSentence.substring(0, 120) + '...';
  operation.description = operation.summary;
  operation.summary = firstSentence;
}

App.controller('Portal', function($scope, spec) {
  $scope.MAX_HIGHLIGHT_LEN = 10000;
  var hash = window.location.hash || START_PAGE;
  $scope.activePage = hash.substring(1);
  $scope.$watch('activePage', function(page) {
    mixpanel.track('set_page_' + page, {
      url: SPEC_URL,
    })
  })
  $scope.stripHtml = function(str) {
    if (!str) return str;
    return str.replace(/<(?:.|\n)*?>/gm, '');
  }

  var PARSER_OPTS = {
    strictValidation: false,
    validateSchema: false
  }

  var initSecurity = function() {
    if ($scope.spec.securityDefinitions) {
      for (var label in $scope.spec.securityDefinitions) {
        def = $scope.spec.securityDefinitions[label];
        if (def.type === 'oauth2') {
          $scope.oauthDefinition = def;
          $scope.startOAuth = function() {
            $('#OAuth2').modal('show');
            mixpanel.track('prompt_oauth', {
              host: $scope.spec.host,
            });
          }
        }
      }
    }
  }

  var initRoutes = function() {
    for (path in $scope.spec.paths) {
      var pathParams = $scope.spec.paths[path].parameters || [];
      for (method in $scope.spec.paths[path]) {
        if (method === 'parameters') continue;
        var operation = $scope.spec.paths[path][method];
        operation.parameters = (operation.parameters || []).concat(pathParams);
        maybeTruncateSummary(operation);
        operation.description = maybeAddExternalDocs(operation.description, operation.externalDocs);
        operation.responses = operation.responses || {};
        var successResponse = operation.responses['200'] = operation.responses['200'] || {};
        if (successResponse.description === 'No response was specified') successResponse.description = '';
        if (!successResponse.description) successResponse.description = 'OK';
        var route = {path: path, method: method, operation: operation};
        route.visual = operation.responses['200'] && operation.responses['200']['x-lucy/view'];
        if (route.visual) $scope.hasVisualRoute = true;
        var joinSearchFields = function(fields) {
          return fields.filter(function(f) {return f}).join(' ').toLowerCase();
        }
        var searchFields = [
          route.path,
          route.method,
          route.operation.description,
          route.operation.summary,
        ];
        searchFields = searchFields.concat(route.operation.parameters.map(function(p) {
          var paramFields = [
            p.in,
            p.name,
            p.description,
          ];
          return joinSearchFields(paramFields);
        }));
        route.searchText = joinSearchFields(searchFields);
        $scope.routes.push(route);
      }
    }
    $scope.routes = $scope.routes.sort(SORT_ROUTES);
  }

  var initTags = function() {
    var uniqueTags = [];
    $scope.routes.forEach(function(route) {
      (route.operation.tags || []).forEach(function(t) {
        if (uniqueTags.indexOf(t) === -1) uniqueTags.push(t);
      })
    });
    $scope.spec.tags = $scope.spec.tags || [];
    uniqueTags.forEach(function(tag) {
      var needToAdd = true;
      $scope.spec.tags.forEach(function(existingTag) {
        if (tag === existingTag.name) needToAdd = false;
      })
      if (needToAdd) $scope.spec.tags.push({name: tag});
    })
    if ($scope.spec.tags.length) {
      $scope.routes.forEach(function(r) {
        r.operation.tags = r.operation.tags || ['default'];
      })
    }
  }

  spec.then(function(spec) {
    $scope.routes = [];
    $scope.setSpec = function(spec) {
      $scope.spec = spec;
      var info = $scope.spec.info = $scope.spec.info || {};
      info.description = maybeAddExternalDocs(info.description, $scope.spec.externalDocs);
      initRoutes();
      initTags();
      initSecurity();
    }
    swagger.parser.parse(spec.data, PARSER_OPTS, function(err, api) {
      if (err) console.log(err);
      api = api || spec.data;
      mixpanel.track('get_swagger', {
        host: api.host,
        url: SPEC_URL,
      });
      $scope.setSpec(api);
      $scope.$apply();
    })

    $scope.setActiveTag = function(tag) {
      $scope.activeTag = tag;
      if ($scope.activePage === 'documentation') {
        $('#Docs').scope().scrollTo(0);
      }
    }

    var promptedOAuth = false;
    $scope.openConsole = function(route) {
      if (route) $('#Console').scope().setActiveRoute(route);
      $scope.activePage = 'console';
      if (!promptedOAuth && $scope.startOAuth) {
        promptedOAuth = true;
        $scope.startOAuth();
      }
    }

    $scope.openDocumentation = function(route) {
      $scope.activePage = 'documentation';
      if (route) {
        $('#Docs').scope().query = '';
        setTimeout(function() {
          $('#Docs').scope().scrollToRoute(route);
        }, 800);
      }
    }

    $scope.openPage = function(page) {
      if (page === 'console') $scope.openConsole();
      else if (page === 'documentation') $scope.openDocumentation();
      else $scope.activePage = page;
    }
  })
});

App.controller('Docs', function($scope) {
  $scope.printSchema = function(schema) {
    return JSON.stringify(EXAMPLES.schemaExample(schema), null, 2);
  }
  $scope.getId = function(verb, path) {
    return verb + '_' + path.replace(/\W/g, '_');
  }
  $scope.initMenu = function () {
    $scope.menuItems = [];
    if ($scope.spec.info['x-lucy/readme'] || $scope.spec.info.description) {
      $scope.menuItems.push({
        title: 'README',
        class: 'readme',
        target: '#README',
      });
    }
    if ($scope.spec.tags && $scope.spec.tags.length) {
      $scope.menuItems = $scope.menuItems.concat($scope.spec.tags.map(function(tag) {
        var children = $scope.routesFiltered.filter(function(r) {
          return r.operation.tags && r.operation.tags.indexOf(tag.name) !== -1;
        }).map(function(route) {
          var index = $scope.routesFiltered.indexOf(route);
          return {
            method: route.method,
            title: route.path,
            class: 'route',
            target: '#ScrollRoute' + index + ' h2',
          }
        })
        if (!children.length) return null;
        return {
          title: tag.name,
          class: 'tag',
          children: children,
        }
      }).filter(function(item) {return item}));
    } else {
      $scope.menuItems = $scope.menuItems.concat($scope.routesFiltered.map(function(route, index) {
        return {
          method: route.method,
          title: route.path,
          class: 'route',
          target: '#ScrollRoute' + index + ' h2',
        }
      }))
    }
  }
  $scope.scrollTo = function(idx) {
    var newTop = 0;
    if (idx !== -1) {
      if ($('#ScrollRoute0').length === 0) return;
      $scope.scrollToTarget('#ScrollRoute' + idx + ' h2');
    }
    $scope.menuItems.active = $scope.menuItems[0];
  }

  $scope.scrollToTarget = function(target) {
    var curTop = $('.docs-col').scrollTop();
    var colTop = $('.docs-col').offset().top;
    var targetTop = $(target).offset().top;
    newTop = targetTop - colTop + curTop - 15;
    
    $scope.animatingScroll = true;
    $('.docs-col').animate({
      scrollTop: newTop
    }, 800, function() {
      $scope.animatingScroll = false;
    })
  }

  $scope.scrollToRoute = function(idx) {
    if (typeof idx === 'object') idx = $scope.routesFiltered.indexOf(idx);
    if (idx === -1) $scope.scrollToTarget('#README');
    else $scope.scrollToTarget('#ScrollRoute' + idx + ' h2');
  }

  $scope.scrollToTag = function(idx) {
    var tag = $scope.scrolledTag = $scope.spec.tags[idx];
    var scrollToRoute = -1;
    $scope.routesFiltered.forEach(function(r, routeIdx) {
      if (r.operation.tags &&
          scrollToRoute === -1 &&
          r.operation.tags.indexOf(tag.name) !== -1) scrollToRoute = routeIdx
    })
    $scope.scrollToRoute(scrollToRoute);
  }
  $scope.onScroll= function() {
    if ($scope.animatingScroll) return;
    if ($scope.activePage !== 'documentation') return;
    var visibleHeight = $('.docs-col').height() - 50;
    var closest = null;
    var minDist = Infinity;
    $('.scroll-target').each(function(index) {
      var thisTop = $(this).offset().top;
      if (closest === null ||
          (minDist < 0 && thisTop < visibleHeight) ||
          (thisTop >= 0 && thisTop < minDist && thisTop < visibleHeight)) {
        closest = $(this);
        minDist = thisTop;
      }
    });
    var id = closest.attr('id');
    $scope.menuItems.active = null;
    var isActive = function(item) {
      return !$scope.menuItems.active && item.target && item.target.indexOf('#' + id) !== -1;
    }
    $scope.menuItems.forEach(function(item) {
      if (isActive(item)) $scope.menuItems.active = item;
      else if (item.children) {
        item.children.forEach(function(child) {
          if (isActive(child)) $scope.menuItems.active = child;
        })
      }
    })
  }

  $scope.routesFiltered = $scope.routes;
  $scope.matchesQuery = function(route) {
    if (!$scope.query) return true;
    var query = $scope.query.toLowerCase();
    var terms = query.split(' ');
    for (var i = 0; i < terms.length; ++i) {
      if (route.searchText.indexOf(terms[i]) === -1) return false;
    }
    return true;
  }
  var sortByTag = function(r1, r2) {
    if (!$scope.spec.tags) return SORT_ROUTES(r1, r2);
    if (r1.operation.tags && !r2.operation.tags) return -1;
    if (r2.operation.tags && !r1.operation.tags) return 1;
    if (!r1.operation.tags && !r2.operation.tags) return SORT_ROUTES(r1, r2);
    var r1Index = -1;
    var r2Index = -1;
    $scope.spec.tags.forEach(function(tag, index) {
      if (r1.operation.tags.indexOf(tag.name) !== -1 &&
          (r1Index === -1 || r1Index > index)) {
        r1Index = index;
      }
      if (r2.operation.tags.indexOf(tag.name) !== -1 &&
          (r2Index === -1 || r2Index > index)) {
        r2Index = index;
      }
    })
    if (r1Index < r2Index) return -1;
    if (r2Index < r1Index) return 1;
    return SORT_ROUTES(r1, r2);
  }
  $scope.routesFiltered = $scope.routes;
  $scope.filterRoutes = function() {
    $scope.routesFiltered = $scope.routes
        .filter($scope.matchesQuery)
        .sort(sortByTag)
    $scope.initMenu();
  }
  $scope.$watch('query', $scope.filterRoutes);
  var filterRoutes = function() {
    $scope.routesFiltered = $scope.routes
        .filter($scope.matchesQuery)
  }
  var filterRoutesAndScroll = function() {
    filterRoutes();
    $scope.scrollTo(0);
  }
  $scope.$watch('query', filterRoutesAndScroll);
  $scope.$watch('activeTag', filterRoutesAndScroll);
  $scope.$watch('routes', filterRoutesAndScroll);
  $scope.query = '';

  $scope.editorMode = false;
  $scope.switchMode = function() {
    $scope.editorMode = !$scope.editorMode;
  }

  $scope.addOperation = function() {
    var path = '/newOperation';
    var i = 0;
    while ($scope.spec.paths[path]) path = '/newOperation' + (++i);
    var op = $scope.spec.paths[path] = {
      parameters: [],
      responses: {
        '200': {
          description: 'Successful Response'
        }
      }
    };
    $scope.routes.push({operation: op, method: 'get', path: path});
    filterRoutes();
    setTimeout(function() {
      $scope.scrollTo($scope.routes.length - 1);
    }, 800);
    $scope.initMenu();
  }
});

App.controller('Route', function($scope) {
  $scope.openConsole = function() {
    $('#Body').scope().activePage = 'console';
    $('#Consoles').scope().activeConsole = $scope.$index;
  }

  $scope.addParameter = function() {
    $scope.route.operation.parameters.push({in: 'query', name: 'myParam', type: 'string'})
  }

  $scope.removeParameter = function(idx) {
    $scope.route.operation.parameters.splice(idx, 1);
  }

  $scope.moveParameter = function(idx, dir) {
    var from = idx;
    var to = idx + dir;
    $scope.route.operation.parameters.splice(
        idx + dir,
        0,
        $scope.route.operation.parameters.splice(idx, 1)[0]
    );
  }

  $scope.addResponse = function() {
    var code = 200;
    while ($scope.route.operation.responses[String(code)]) ++code;
    $scope.route.operation.responses[String(code)] = {}
  }

  $scope.removeResponse = function(code) {
    delete $scope.route.operation.responses[code];
  }
});

